mapper.delete:
delete
from F_Img
where m_id=#{m_id}
and	uuid=#{uuid}

delete
from b_img
where bno=#{bno}
and	uuid=#{uuid}

mapper.select:
SELECT t.*,
      'c:\upload\' || uploadpath || uuid || '_' || filename AS savePath,
DECODE(filetype, 'I', 'c:\upload\' || uploadpath || 's_' || uuid || '_' || filename, 'NO_image') AS s_savePath
FROM b_img t
WHERE bno = #{bno}
AND uuid = #{uuid}
