mapper.select:
select * from (select t.*, rownum rn from(select h.h_id, pname, addr, substr(openhour,1, 30) openhour, substr(parkyn,-2) ParkYN , nvl(star,0) star, nvl(cnt,0) cnt, savePath from hospital h LEFT JOIN (select h_id, round(avg(star)) star, count(review) cnt from h_review group by h_id) r ON h.h_id = r.h_id left join (select h_id, uploadpath||h_id||filename savePath from h_img) s ON h.h_id = s.h_id where 1=1 and savePath is not null
and addr like '%'||#{megaregion}||'%'
and addr like '%'||#{smallregion}||'%'
) t ) where rn between #{startNo} and #{endNo}
