mapper.select:

select *
	from(
			select t.*, rownum rn
			from (
					SELECT R.REPLY, R.REGDATE, M.NICKNAME,R.RNO
					FROM REPLY R
					JOIN BOARD B ON R.BNO = B.BNO
					JOIN MEMBER M ON R.M_ID = M.M_ID
					WHERE B.BNO = #{bno}
					ORDER BY
					    r.rno DESC

				 ) t
		)
where rn between #{cri.startNo} and #{cri.endNo}

mapper.update:
update reply 
   set reply= #{reply}
     , updatedate = sysdate
 where rno = #{rno}

mapper.select:
select count(*) 
from reply
where bno= #{bno}
